css:
/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
h1,
h2,
h3 {
  line-height: 1em;
  margin-bottom: 15px;
}
h1 {
  font-size: 2em;
}
h2 {
  font-size: 1.5em;
}
h3 {
  font-size: 1.2em;
}
p {
  line-height: 1.6;
  margin-bottom: 15px;
}
label {
  font-weight: normal;
}
pre {
  box-shadow: none;
  color: #aaaaaa;
  background: #1d1f21;
  border-radius: 4px;
  border: none;
  margin: 0;
}
code {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  background: #484d52;
  border-radius: 4px;
}
.selected {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.text-smaller {
  font-size: 0.9em;
}
.text-subtle {
  color: #6d6d6d;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.text-highlight {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.text-error {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #cc0000;
}
.text-info {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #5293d8;
}
.text-info:hover {
  color: #5293d8;
}
.text-warning {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #ff982d;
}
.text-warning:hover {
  color: #ff982d;
}
.text-success {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #26c26a;
}
.text-success:hover {
  color: #26c26a;
}
.highlight {
  color: #ffffff;
  font-weight: normal;
  text-shadow: none;
  background-color: #484d52;
  border-radius: 4px;
  padding: 1px 3px;
}
.highlight-info {
  color: #ffffff;
  font-weight: normal;
  text-shadow: none;
  background-color: #5293d8;
  border-radius: 4px;
  padding: 1px 3px;
}
.highlight-warning {
  color: #ffffff;
  font-weight: normal;
  text-shadow: none;
  background-color: #ff982d;
  border-radius: 4px;
  padding: 1px 3px;
}
.highlight-error {
  color: #ffffff;
  font-weight: normal;
  text-shadow: none;
  background-color: #cc0000;
  border-radius: 4px;
  padding: 1px 3px;
}
.highlight-success {
  color: #ffffff;
  font-weight: normal;
  text-shadow: none;
  background-color: #26c26a;
  border-radius: 4px;
  padding: 1px 3px;
}
