css:
.editor-contents {
  border-bottom-left-radius: 4px;
}
atom-text-editor[mini] {
  background-color: #1d1f21;
  border-radius: 4px;
  height: 28px;
  padding: 4px;
  line-height: 1.3em;
}
:host,
atom-text-editor,
atom-text-editor::shadow {
  background-color: #1d1f21 !important;
  -webkit-font-smoothing: antialiased;
}
:host .scroll-view,
atom-text-editor .scroll-view,
atom-text-editor::shadow .scroll-view {
  padding: 0;
}
:host .wrap-guide,
atom-text-editor .wrap-guide,
atom-text-editor::shadow .wrap-guide {
  background: #2b2e31;
}
:host .indent-guide,
atom-text-editor .indent-guide,
atom-text-editor::shadow .indent-guide {
  color: #2b2e31;
}
:host .invisible-character,
atom-text-editor .invisible-character,
atom-text-editor::shadow .invisible-character {
  color: #2b2e31;
}
:host .invisible,
atom-text-editor .invisible,
atom-text-editor::shadow .invisible {
  color: #2b2e31;
}
:host .cursor,
atom-text-editor .cursor,
atom-text-editor::shadow .cursor {
  color: #ffffff;
}
:host .selection .region,
atom-text-editor .selection .region,
atom-text-editor::shadow .selection .region {
  background-color: rgba(65, 130, 196, 0.3);
}
:host .gutter,
atom-text-editor .gutter,
atom-text-editor::shadow .gutter {
  background-color: #242729 !important;
  color: #6d6d6d;
  border-right: 0;
  margin: 0;
}
:host .gutter .line-number,
atom-text-editor .gutter .line-number,
atom-text-editor::shadow .gutter .line-number {
  background-color: #242729 !important;
  padding-left: 8px;
  padding-top: 0px;
}
:host .gutter .line-number.git-line-added,
atom-text-editor .gutter .line-number.git-line-added,
atom-text-editor::shadow .gutter .line-number.git-line-added {
  border-left-color: #26c26a !important;
}
:host .gutter .line-number.git-line-modified,
atom-text-editor .gutter .line-number.git-line-modified,
atom-text-editor::shadow .gutter .line-number.git-line-modified {
  border-left: 2px solid #5293d8 !important;
}
:host .gutter .line-number.git-line-removed,
atom-text-editor .gutter .line-number.git-line-removed,
atom-text-editor::shadow .gutter .line-number.git-line-removed {
  border-left-color: #cc0000 !important;
}
:host .gutter .line-number.cursor-line,
atom-text-editor .gutter .line-number.cursor-line,
atom-text-editor::shadow .gutter .line-number.cursor-line {
  background-color: #4182c4 !important;
  color: #ffffff !important;
}
:host .gutter .line-number.cursor-line-no-selection,
atom-text-editor .gutter .line-number.cursor-line-no-selection,
atom-text-editor::shadow .gutter .line-number.cursor-line-no-selection {
  background-color: #1d1f21;
  color: #ffffff;
}
:host .gutter .line-number.folded,
atom-text-editor .gutter .line-number.folded,
atom-text-editor::shadow .gutter .line-number.folded,
:host .gutter .line-number:after,
atom-text-editor .gutter .line-number:after,
atom-text-editor::shadow .gutter .line-number:after,
:host .fold-marker:after,
atom-text-editor .fold-marker:after,
atom-text-editor::shadow .fold-marker:after {
  color: #ffffff;
}
.select-list.popover-list {
  background-color: #2b2e31;
  border-radius: 4px;
}
.select-list.popover-list ol.list-group {
  padding: 4px;
}
.select-list.popover-list ol.list-group li {
  transition: color 200ms linear, background 200ms linear;
  font-weight: normal;
  font-size: 1em;
  letter-spacing: 0;
  color: #aaaaaa;
}
.select-list.popover-list ol.list-group li.selected {
  font-weight: normal;
  font-size: 1em;
  letter-spacing: 0;
  color: #ffffff;
}
.select-list.popover-list ol.list-group li.selected:before {
  background-color: #4182c4;
}
.select-list.popover-list ol.list-group li:before {
  transition: color 200ms linear, background-color 200ms linear;
  content: '';
  width: 100%;
  background-color: #2b2e31;
}
.select-list.popover-list ol.list-group li:hover {
  color: #ffffff;
}
.select-list.popover-list ol.list-group li:hover:before {
  background-color: #484d52;
}
