/*
* This is sortof prepared for adjustable themes.
* Everything is based of a single color taken from the syntax theme.
*/
atom-notifications {
  display: block;
  z-index: 1000;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  padding: 1.33rem;
  font-size: 1.2em;
  overflow-x: hidden;
  overflow-y: auto;
  pointer-events: none;
}
atom-notifications::-webkit-scrollbar {
  display: none;
}
atom-notifications atom-notification .close-all {
  display: none;
}
atom-notifications atom-notification:first-child .close-all {
  display: block;
}
atom-notifications atom-notification:first-child.has-close .message {
  padding-right: 97.66rem;
}
atom-notifications atom-notification:only-child .close-all {
  display: none;
}
atom-notifications atom-notification:only-child.has-close .message {
  padding-right: 25.33rem;
}
atom-notification {
  float: right;
  clear: right;
  position: relative;
  width: 450px;
  padding-left: 30px;
  max-height: 800px;
  word-wrap: break-word;
  pointer-events: auto;
}
atom-notification.icon:before {
  position: absolute;
  top: 0;
  left: 0;
  width: 30px;
  height: calc(100% - 2px);
  padding-top: 1.33rem;
  text-align: center;
  border-radius: 2px 0 0 2px;
}
atom-notification .meta,
atom-notification .close,
atom-notification .detail,
atom-notification .stack-toggle,
atom-notification .stack-container {
  display: none;
}
atom-notification.fatal .meta,
atom-notification.has-close .close,
atom-notification.has-detail .detail,
atom-notification.has-stack .stack-toggle,
atom-notification.has-stack .stack-container {
  display: block;
}
atom-notification .item {
  padding: 1.33rem;
  border-top: 1px solid rgba(0, 0, 0, 0.1);
}
atom-notification .item.message {
  border-top: none;
}
atom-notification .item.message p:last-child {
  margin-bottom: 0;
}
atom-notification.has-close .message {
  padding-right: 25.33rem;
}
atom-notification .content {
  margin-bottom: 2px;
  border-radius: 0 2px 2px 0;
}
atom-notification .message {
  max-height: 200px;
  overflow-y: auto;
}
atom-notification .close-all.btn {
  position: absolute;
  top: 7px;
  right: 38px;
  background: none;
}
atom-notification .close {
  position: absolute;
  top: 0;
  right: 0;
  width: 38px;
  height: 38px;
  line-height: 38px;
  text-align: center;
  font-size: 16px;
  text-shadow: none;
  color: inherit;
  opacity: .3;
}
atom-notification .close:hover,
atom-notification .close:focus {
  opacity: 1;
  color: inherit;
}
atom-notification .close:active {
  opacity: .2;
}
atom-notification .close:before {
  margin: 0;
  color: inherit;
}
atom-notification.has-detail {
  width: 450px;
}
atom-notification .detail {
  font-size: .8em;
  background-color: rgba(255, 255, 255, 0.3);
  background-clip: padding-box;
  max-height: 500px;
  overflow-y: auto;
}
atom-notification .detail .line {
  white-space: pre;
  font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
}
atom-notification .detail .stack-toggle {
  display: block;
  margin-top: 1.33rem;
}
atom-notification .detail .stack-toggle .icon:before {
  margin: 0;
}
atom-notification .detail .stack-container {
  margin-top: 1.33rem;
}
atom-notification .fatal-notification {
  font-size: .8em;
}
atom-notification .btn-toolbar {
  margin-top: 1.33rem;
}
atom-notification .btn-copy-report {
  vertical-align: middle;
}
atom-notification.fatal .content {
  color: #240a05;
  background-color: #cb5656;
}
atom-notification.fatal a {
  color: #7e2212;
}
atom-notification.fatal code {
  color: #240a05;
  background-color: #bb4242;
}
atom-notification.fatal.icon:before {
  color: #d88181;
  background-color: #7b2626;
}
atom-notification.fatal .close-all.btn {
  border: 1px solid rgba(36, 10, 5, 0.3);
  color: rgba(36, 10, 5, 0.6);
  text-shadow: none;
}
atom-notification.fatal .close-all.btn:hover {
  background: none;
  border-color: rgba(36, 10, 5, 0.8);
  color: #240a05;
}
atom-notification.error .content {
  color: #240a05;
  background-color: #cb5656;
}
atom-notification.error a {
  color: #7e2212;
}
atom-notification.error code {
  color: #240a05;
  background-color: #bb4242;
}
atom-notification.error.icon:before {
  color: #d88181;
  background-color: #7b2626;
}
atom-notification.error .close-all.btn {
  border: 1px solid rgba(36, 10, 5, 0.3);
  color: rgba(36, 10, 5, 0.6);
  text-shadow: none;
}
atom-notification.error .close-all.btn:hover {
  background: none;
  border-color: rgba(36, 10, 5, 0.8);
  color: #240a05;
}
atom-notification.warning .content {
  color: #342714;
  background-color: #c2945e;
}
atom-notification.warning a {
  color: #7e5f30;
}
atom-notification.warning code {
  color: #342714;
  background-color: #b1824c;
}
atom-notification.warning.icon:before {
  color: #d2af87;
  background-color: #75532c;
}
atom-notification.warning .close-all.btn {
  border: 1px solid rgba(52, 39, 20, 0.3);
  color: rgba(52, 39, 20, 0.6);
  text-shadow: none;
}
atom-notification.warning .close-all.btn:hover {
  background: none;
  border-color: rgba(52, 39, 20, 0.8);
  color: #342714;
}
atom-notification.info .content {
  color: #0a1427;
  background-color: #3d96e4;
}
atom-notification.info a {
  color: #1f3e78;
}
atom-notification.info code {
  color: #0a1427;
  background-color: #2684d7;
}
atom-notification.info.icon:before {
  color: #6eb0ea;
  background-color: #14558d;
}
atom-notification.info .close-all.btn {
  border: 1px solid rgba(10, 20, 39, 0.3);
  color: rgba(10, 20, 39, 0.6);
  text-shadow: none;
}
atom-notification.info .close-all.btn:hover {
  background: none;
  border-color: rgba(10, 20, 39, 0.8);
  color: #0a1427;
}
atom-notification.success .content {
  color: #0b160f;
  background-color: #56cba4;
}
atom-notification.success a {
  color: #2d5a3c;
}
atom-notification.success code {
  color: #0b160f;
  background-color: #42bb92;
}
atom-notification.success.icon:before {
  color: #81d8bb;
  background-color: #267b5f;
}
atom-notification.success .close-all.btn {
  border: 1px solid rgba(11, 22, 15, 0.3);
  color: rgba(11, 22, 15, 0.6);
  text-shadow: none;
}
atom-notification.success .close-all.btn:hover {
  background: none;
  border-color: rgba(11, 22, 15, 0.8);
  color: #0b160f;
}
atom-notification {
  -webkit-animation: notification-show 0.16s cubic-bezier(0.175, 0.885, 0.32, 1.27499);
}
atom-notification[type="fatal"] {
  -webkit-animation: notification-show 0.16s cubic-bezier(0.175, 0.885, 0.32, 1.27499), notification-shake 4s 2s;
  -webkit-animation-iteration-count: 1, 3;
}
atom-notification[type="fatal"]:hover {
  -webkit-animation-play-state: paused;
}
atom-notification.remove,
atom-notification.remove:hover {
  -webkit-animation: notification-hide 0.24s cubic-bezier(0.33859, -0.42, 1, -0.22), notification-shrink 0.24s 0.24s cubic-bezier(0.5, 0, 0, 1);
  -webkit-animation-fill-mode: forwards;
}
@-webkit-keyframes notification-show {
  0% {
    opacity: 0;
    transform: perspective(450px) translate(0, -30px) rotateX(90deg);
  }
  100% {
    opacity: 1;
    transform: perspective(450px) translate(0, 0) rotateX(0deg);
  }
}
@-webkit-keyframes notification-hide {
  0% {
    opacity: 1;
    transform: scale(1);
  }
  100% {
    opacity: 0;
    transform: scale(0.8);
  }
}
@-webkit-keyframes notification-shrink {
  0% {
    opacity: 0;
    max-height: 800px;
    transform: scale(0.8);
  }
  100% {
    opacity: 0;
    max-height: 0;
    transform: scale(0.8);
  }
}
@-webkit-keyframes notification-shake {
  0% {
    transform: translateX(0);
  }
  2% {
    transform: translateX(-4px);
  }
  4% {
    transform: translateX(8px);
  }
  6% {
    transform: translateX(-4px);
  }
  8% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(0);
  }
}
