css:
.btn {
  transition: color 200ms linear, background 200ms linear;
  border-bottom: 1px solid #1d1f21;
  color: #aaaaaa;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.btn.selected {
  background-color: #4182c4;
  border-bottom: 1px solid #3a3e42;
}
.btn.btn-primary {
  color: #aaaaaa;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.btn.btn-primary:hover {
  border-color: #242729;
}
.btn.btn-primary.selected {
  background-color: #4182c4;
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.btn.btn-info {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #5293d8;
}
.btn.btn-info:hover {
  border-color: #5293d8;
}
.btn.btn-info.selected {
  background-color: #5293d8;
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.btn.btn-success {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #26c26a;
}
.btn.btn-success:hover {
  border-color: #26c26a;
}
.btn.btn-success.selected {
  background-color: #26c26a;
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.btn.btn-warning {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #ff982d;
}
.btn.btn-warning:hover {
  border-color: #ff982d;
}
.btn.btn-warning.selected {
  background-color: #ff982d;
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.btn.btn-error {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #cc0000;
}
.btn.btn-error:hover {
  border-color: #cc0000;
}
.btn.btn-error.selected {
  background-color: #cc0000;
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.btn.btn-error.selected:hover {
  border: 0 !important;
}
.btn:hover {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
button:disabled {
  color: #1d1f21 !important;
}
