css:
.tab-bar {
  font-size: 1em;
  background-color: #b0b0b0;
  background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0.5) 0%, rgba(0, 0, 0, 0.2) 35%, rgba(0, 0, 0, 0.2) 100%);
  top: 0;
  left: 0;
  height: 2.5em;
  z-index: 100;
  width: 100%;
}
.tab-bar span {
  color: #030303;
}
.tab-bar .tab {
  padding: 0 0 0 0;
  font-size: 1.2em;
  min-width: 4em;
  max-width: 25%;
  min-width: 0em;
  border-right: 1px solid rgba(0, 0, 0, 0.5);
  color: #434343;
}
.tab-bar .tab .title {
  text-align: center;
  font-weight: bold;
  padding: .3em 1.5em .1em;
  text-shadow: 0 1px 0px rgba(255, 255, 255, 0.3);
}
.tab-bar .tab .close-icon {
  left: -0.5em;
  top: 0;
  font-size: 1em;
  line-height: 2.0em;
  height: 2em;
  width: 3em;
  text-align: center;
  display: none;
}
.tab-bar .tab .close-icon:before {
  font-size: 1em;
  content: "\f081";
}
.tab-bar .tab:hover .close-icon {
  color: #434343;
  display: inline;
}
.tab-bar .tab:last-child {
  border-right: none;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 0;
  left: .66em;
  display: inline;
  border: none;
}
.tab-bar .tab.modified:not(:hover) .close-icon:before {
  content: "\f052";
}
.tab-bar .tab.modified:hover .close-icon {
  color: #434343;
}
.tab-bar .tab.active {
  background-color: #b0b0b0;
  background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.3));
  color: #434343;
  -webkit-flex: 1;
  border-top: none;
  border-right: 1px solid rgba(0, 0, 0, 0.5);
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
}
.tab-bar .tab.active .close-icon {
  color: #434343;
}
.directory.selected .header:first-child span {
  color: #434343;
}
.directory.selected:before {
  border-top: 1px solid #67a2dd;
  border-bottom: 1px solid #4277c0;
  background-color: #5f9cdb;
  background-image: linear-gradient(to bottom, #7bb8e8, #4884d0);
  height: 1.9em;
}
.tree-view-resizer {
  background: #e8e8e8;
  border-top: 1px solid rgba(0, 0, 0, 0.5);
  border-right: 1px solid rgba(0, 0, 0, 0.5);
}
.tree-view-resizer span {
  color: #030303;
}
.icon:before {
  top: .2em!important;
  position: relative;
}
.icon-tools:before {
  top: 1px!important;
  position: relative;
  font-family: 'Octicons Regular';
  content: "\f02f";
  font-weight: normal;
  color: #434343 !important;
}
.icon-file-directory:before,
.icon-repo:before {
  content: url('data:image/svg+xml;base64,');
}
.icon-file-text:before {
  content: url('data:image/svg+xml;base64,');
}
.icon-file-media:before {
  content: url('data:image/svg+xml;base64,');
}
.icon-file-symlink-file:before {
  content: url('data:image/svg+xml;base64,');
}
.tree-view {
  font-size: 1.1em;
  padding: 0;
}
.tree-view .entry:before {
  background-color: #5f9cdb;
}
.tree-view .file,
.tree-view .directory {
  position: relative;
}
.tree-view .directory.selected:before {
  left: -30em;
}
.tree-view .file.selected span {
  color: #ffffff;
  text-shadow: 0 1px 1px rgba(0, 0, 0, 0.5);
}
.tree-view .file.selected .icon:before {
  color: #fafbfd;
}
.tree-view .file.selected:before {
  position: absolute;
  left: -30em;
  top: 1px;
  border-top: 1px solid #67a2dd;
  border-bottom: 1px solid #4277c0;
  background-color: #5f9cdb;
  background-image: linear-gradient(to bottom, #7bb8e8, #4884d0);
  height: 1.9em;
}
.icon:before {
  color: #687382;
}
.list-group li:not(.list-nested-item),
.list-tree li:not(.list-nested-item),
.list-group li.list-nested-item > .list-item,
.list-tree li.list-nested-item > .list-item {
  padding: 0 .5em;
  line-height: 1.9em;
}
.directory .header {
  position: relative;
}
.directory:not(.selected) .header:first-child span {
  color: #030303;
  text-shadow: none;
}
.directory.selected .header:first-child span {
  color: #ffffff;
  text-shadow: 0 1px 1px rgba(0, 0, 0, 0.5);
}
.directory.selected .directory:not(.selected) .header:first-child span {
  color: #030303;
  text-shadow: none;
}
.list-tree.has-collapsable-children .list-nested-item.collapsed > .list-item:before {
  position: relative;
  top: 2px;
  left: 0;
  content: url('data:image/svg+xml;base64,') !important;
}
.list-tree.has-collapsable-children .list-nested-item > .list-item:before {
  top: 2px;
  content: url('data:image/svg+xml;base64,') !important;
}
.select-list .list-group .selected {
  background: linear-gradient(to bottom, #7bb8e8, #4884d0) !important;
  border-radius: .2em;
}
.status.icon:before {
  top: 0!important;
}
.selected .status.icon:before {
  color: #fff;
}
progress {
  -webkit-appearance: none;
  margin-right: 1em;
}
progress::-webkit-progress-bar {
  background-color: #e8e8e8;
  background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.1), rgba(0, 0, 0, 0));
  border-radius: .2em;
  box-shadow: inset 0 0 0.1em #b0b0b0;
}
progress::-webkit-progress-value {
  background-color: #b0b0b0;
  background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.3)), -webkit-linear-gradient(left, #b0b0b0, #5f9cdb);
  border-radius: .2em;
}
progress:not([value])::-webkit-progress-bar {
  -webkit-animation: animate-stripes 5s linear infinite;
  animation: animate-stripes 5s linear infinite;
  background-image: -webkit-linear-gradient(-45deg, transparent 33%, rgba(0, 0, 0, 0.1) 33%, rgba(0, 0, 0, 0.1) 66%, transparent 66%), -webkit-linear-gradient(top, rgba(255, 255, 255, 0.25), rgba(0, 0, 0, 0.25)), -webkit-linear-gradient(left, #b0b0b0, #5f9cdb);
  background-size: 2em 1em, 100% 100%, 100% 100%;
}
[class*="spinner"] {
  -webkit-animation-name: spin;
  -webkit-animation-duration: 4000ms;
  -webkit-animation-iteration-count: infinite;
  -webkit-animation-timing-function: linear;
  animation-name: spin;
  animation-duration: 4000ms;
  animation-iteration-count: infinite;
  animation-timing-function: linear;
}
.loading:before {
  font-family: 'Octicons Regular';
  content: "\f02f";
  font-weight: normal;
  padding: 0;
  -webkit-animation-name: spin;
  -webkit-animation-duration: 4000ms;
  -webkit-animation-iteration-count: infinite;
  -webkit-animation-timing-function: linear;
  animation-name: spin;
  animation-duration: 4000ms;
  animation-iteration-count: infinite;
  animation-timing-function: linear;
}
.loading:before .loading-message:before {
  content: "";
}
.select-list .loading .loading-message:before {
  content: "";
  margin-right: .5em;
  height: 0;
  width: 0;
}
.loading-spinner-small {
  font-size: 2.1em;
}
.loading-spinner-medium {
  font-size: 4.2em;
}
.loading-spinner-large {
  font-size: 6.4em;
}
@-webkit-keyframes animate-stripes {
  100% {
    background-position: 100px 0px;
  }
}
@keyframes animate-stripes {
  100% {
    background-position: 100px 0px;
  }
}
@-webkit-keyframes spin {
  from {
    -webkit-transform: rotate(0deg);
  }
  to {
    -webkit-transform: rotate(360deg);
  }
}
@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}
.btn {
  border-radius: .2em;
  font-weight: bold;
  background-color: #e8e8e8;
  background-image: linear-gradient(to bottom, #e0e0e0 0%, #d7d7d7 3%, #b2b2b2 100%);
  color: #434343;
  text-shadow: 0 1px 0px rgba(255, 255, 255, 0.5);
  border: 1px solid rgba(0, 0, 0, 0.5);
  box-shadow: 0 0px 1px rgba(0, 0, 0, 0.2);
}
.btn:hover {
  color: #030303;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.2);
}
.btn-xs,
.btn-group-xs .btn {
  font-weight: normal;
}
[class*=highlight] {
  padding: .2em .5em;
}
.btn-primary,
.btn-primary:hover,
.btn-primary.selected,
.highlight {
  color: #434343;
}
.btn-info:before,
.btn-info:hover:before,
.btn-info.selected:before,
.highlight-info:before {
  font-family: 'Octicons Regular';
  content: "\f026";
  font-weight: normal;
  padding-right: .5em;
}
.btn-success:before,
.btn-success:hover:before,
.btn-success.selected:before,
.highlight-success:before {
  font-family: 'Octicons Regular';
  content: "\f03a";
  font-weight: normal;
  padding-right: .5em;
}
.btn.selected {
  background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.2));
}
.btn-warning:before,
.btn-warning:hover:before,
.btn-warning.selected:before,
.highlight-warning:before {
  font-family: 'Octicons Regular';
  content: "\f02d";
  font-weight: normal;
  padding-right: .5em;
}
.btn-error:before,
.btn-error:hover:before,
.btn-error.selected:before,
.highlight-error:before {
  font-family: 'Octicons Regular';
  content: "\f08f ";
  font-weight: normal;
  padding-right: .5em;
}
.text-highlight {
  padding: 0;
  color: #5f9cdb;
}
.text-info {
  color: #434343;
}
.text-success {
  color: #229922;
}
.text-warning {
  color: #ffcc33;
}
.text-error {
  color: #cc3333;
}
.btn-group .btn {
  border: 1px solid rgba(0, 0, 0, 0.5);
  box-shadow: 0 0px 1px rgba(0, 0, 0, 0.2);
}
.btn-group > .btn:first-child {
  border-left: 1px solid rgba(0, 0, 0, 0.5);
}
.btn-group > .btn:last-child {
  border-right: 1px solid rgba(0, 0, 0, 0.5);
}
.btn-group > .btn:hover {
  z-index: inherit;
}
.btn-group .btn.selected {
  background-color: #5f9cdb;
  background-image: linear-gradient(to bottom, #7bb8e8, #4884d0);
  color: #434343;
}
.btn-group .btn.selected:last-child {
  border-right: 1px solid rgba(0, 0, 0, 0.5);
}
.tooltip.top .tooltip-arrow {
  border-top-color: rgba(255, 255, 255, 0.95);
}
.tooltip.bottom .tooltip-arrow {
  border-bottom-color: rgba(255, 255, 255, 0.95);
}
.tooltip .tooltip-inner {
  background: rgba(255, 255, 255, 0.95);
  color: #333333;
  max-width: 48em;
  padding: .5em .8em;
}
.select-list {
  background: rgba(255, 255, 255, 0.95);
  color: #333333;
  max-width: 48em;
  padding: .5em .8em;
