}
.select-list.popover-list,
.select-list .list-group {
  position: absolute;
  border-radius: .2em;
}
.select-list.popover-list .selected,
.select-list .list-group .selected {
  background: #5f9cdb;
}
.select-list.popover-list .selected span,
.select-list .list-group .selected span {
  color: #ffffff;
}
.select-list {
