* {
  box-sizing: border-box;
}
body {
  font-size: 10px;
}
.inline-block {
  margin-right: 1em;
}
h1,
h2,
h3 {
  line-height: 1em;
  margin-bottom: 15px;
}
h1 {
  font-size: 2em;
}
h2 {
  font-size: 1.5em;
}
h3 {
  font-size: 1.2em;
}
p {
  line-height: 1.6;
  margin-bottom: 15px;
  color: #030303;
}
label {
  font-weight: normal;
  color: #030303;
}
.mini {
  padding-left: 1em;
}
.icon {
  display: inline-block;
}
.workspace {
  background: #e8e8e8;
  color: #030303;
  padding: 0 0 2.5em 0;
}
.tool-panel {
  background: #e8e8e8;
  color: #030303;
}
.status-bar {
  height: 2.25em;
  background-image: linear-gradient(to bottom, #e0e0e0 0%, #d7d7d7 3%, #b2b2b2 100%);
  border-top: 1px solid rgba(0, 0, 0, 0.5);
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  z-index: 100;
}
.status-bar a {
  color: #434343;
}
.block .editor {
  padding: 0 .75em;
}
.section .section-heading {
  color: red;
}
.ui-site-1 {
  background: red;
}
.ui-site-2 {
  background: orange;
}
.ui-site-3 {
  background: yellow;
}
.ui-site-4 {
  background: green;
}
.ui-site-5 {
  background: blue;
}
.panes .pane .item-views .pane-item {
  background: #e8e8e8;
}
.example {
  border-color: #b0b0b0;
}
.example .example-rendered {
  background: #e8e8e8;
}
.example .example-code {
  border-color: #b0b0b0;
}
.styleguide section .section-heading {
  color: #030303;
}
