.editor .inline-message {
  z-index: 1;
  position: absolute;
  height: auto;
  margin-top: 5px;
  background-color: #323232;
  border: 1px solid #888888;
  color: #ffffff;
}
.editor .inline-message.comment-warning {
  border-color: #323232;
  background-color: rgba(50, 50, 50, 0.95);
  box-shadow: 0px 3px 6px 3px #161719;
  border-radius: 5px;
  padding: 5px;
  padding-bottom: 0px;
}
.editor .inline-message.comment-warning:before {
  position: absolute;
  width: 10px;
  height: 10px;
  content: " ";
  top: -10px;
  left: 20px;
  margin-left: -5px;
  border-left: 5px solid transparent;
  border-right: 5px solid transparent;
  border-bottom: 5px solid #323232;
  z-index: 20;
}
.editor .inline-message.comment-warning .message-content(:last-of-type) {
  margin-bottom: 0px;
}
.editor .inline-message.comment-warning .message-content {
  white-space: pre-wrap;
  font-family: Helvetica;
  font-size: 0.8em;
  font-weight: 300;
  color: #ffffff;
  margin-bottom: 5px;
}
.editor .inline-message.comment-warning .message-content .message-source {
  background-color: #816435;
  display: inline-block;
  font-family: Helvetica;
  font-size: 1.0em;
  padding-left: 5px;
  padding-right: 5px;
  border-radius: 5px;
  margin-right: 5px;
}
.editor .inline-message.comment-error {
  border-color: #323232;
  background-color: rgba(50, 50, 50, 0.95);
  box-shadow: 0px 3px 6px 3px #161719;
  border-radius: 5px;
  padding: 5px;
  padding-bottom: 0px;
}
.editor .inline-message.comment-error:before {
  position: absolute;
  width: 10px;
  height: 10px;
  content: " ";
  top: -10px;
  left: 20px;
  margin-left: -5px;
  border-left: 5px solid transparent;
  border-right: 5px solid transparent;
  border-bottom: 5px solid #323232;
  z-index: 20;
}
.editor .inline-message.comment-error .message-content(:last-of-type) {
  margin-bottom: 0px;
}
.editor .inline-message.comment-error .message-content {
  white-space: pre-wrap;
  font-family: Helvetica;
  font-size: 0.8em;
  font-weight: 300;
  color: #ffffff;
  margin-bottom: 5px;
}
.editor .inline-message.comment-error .message-content .message-source {
  background-color: #8f4242;
  display: inline-block;
  font-family: Helvetica;
  font-size: 1.0em;
  padding-left: 5px;
  padding-right: 5px;
  border-radius: 5px;
  margin-right: 5px;
}
.zen .editor,
.editor-contents,
.scroll-view {
  overflow: visible;
}
.zen .editor.react .scroll-view {
  overflow: visible;
}
