progress {
  height: 16px;
  -webkit-appearance: none;
}
progress::-webkit-progress-bar {
  height: 16px;
  background-color: #666;
  background-image: -webkit-linear-gradient(-30deg, transparent 33%, rgba(0, 0, 0, 0.1) 33%, rgba(0, 0, 0, 0.1) 66%, transparent 66%), -webkit-linear-gradient(top, rgba(255, 255, 255, 0.15), rgba(0, 0, 0, 0.15));
  border-radius: 2px;
  background-size: 25px 16px, 100% 100%, 100% 100%;
  -webkit-animation: animate-stripes 5s linear infinite;
  animation: animate-stripes 5s linear infinite;
}
progress::-webkit-progress-value {
  border-radius: 2px;
  background-image: -webkit-linear-gradient(top, rgba(255, 255, 255, 0.15), rgba(0, 0, 0, 0.15));
  background-color: #17ca65;
}
progress[value]::-webkit-progress-bar {
  background-image: -webkit-linear-gradient(top, rgba(255, 255, 255, 0.15), rgba(0, 0, 0, 0.15));
  animation: none;
}
@-webkit-keyframes animate-stripes {
  100% {
    background-position: 100px 0px;
  }
}
