}
.btn.btn-info {
  color: #ffffff;
  background: #4d5154;
}
.btn.btn-info:hover {
  color: #ffffff;
}
.btn.btn-info.selected,
.btn.btn-info.selected:hover {
  color: #ffffff;
  background: #595e61;
}
.btn.btn-success {
  color: #ffffff;
  background: #4d5154;
}
.btn.btn-success:hover {
  color: #ffffff;
}
.btn.btn-success.selected,
.btn.btn-success.selected:hover {
  color: #ffffff;
  background: #595e61;
}
.btn.btn-warning {
  color: #ffffff;
