.caret {
  border-top: 5px solid #fff;
  margin-top: -1px;
}
.editor.mini {
  -webkit-transition: all 200ms ease;
  box-shadow: inset 0 0 0 1px rgba(26, 26, 26, 0.5);
  color: #ffffff;
  background-color: #212224;
  padding-left: 10px;
  border-radius: 6px;
}
.editor.mini .selection .region {
