}
.command-palette,
.grammar-selector,
.fuzzy-finder,
.project-manager {
  border: none;
  box-shadow: inset 0 1px 0 0 rgba(0, 0, 0, 0.2);
  padding: 5px 0 0 0 !important;
  opacity: .92 !important;
}
.command-palette.from-top,
.grammar-selector.from-top,
.fuzzy-finder.from-top,
.project-manager.from-top {
