  color: #d6d6d6;
  text-shadow: none;
  color: #c9c9c9;
}
.list-group li:not(.list-nested-item) .icon:before,
.list-tree li:not(.list-nested-item) .icon:before,
.list-group li.list-nested-item > .list-item .icon:before,
.list-tree li.list-nested-item > .list-item .icon:before {
  color: #4d5154;
  opacity: .8;
  text-shadow: none;
}
.list-group li:not(.list-nested-item).text-subtle,
.list-tree li:not(.list-nested-item).text-subtle,
.list-group li.list-nested-item.text-subtle > .list-item,
.list-tree li.list-nested-item.text-subtle > .list-item {
