}
.select-list.popover-list {
  background-color: #212326;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
  padding: 5px;
  border-radius: 6px;
  border: 1px solid rgba(255, 255, 255, 0.07);
}
.select-list.popover-list .editor {
