}
.overlay {
  color: #d6d6d6;
  background-color: #212326;
  padding: 5px;
  padding-top: 10px;
  border: 1px solid rgba(255, 255, 255, 0.07);
  opacity: .90;
  border-bottom-right-radius: 6px;
  border-bottom-left-radius: 6px;
}
.overlay .editor.mini {
  margin-bottom: 10px;
}
.overlay .select-list ol.list-group,
.overlay.select-list ol.list-group {
