  text-shadow: none;
  border-bottom: 1px solid rgba(0, 0, 0, 0.6);
  border-top: 1px solid rgba(255, 255, 255, 0.17);
  background-color: transparent;
  background-image: -webkit-linear-gradient(#43484d, #2b2f32);
}
.panel-heading .btn {
  padding-left: 8px;
  padding-right: 8px;
  color: #f0f0f0;
