.tab-bar {
  height: 30px;
  background: #25282c;
  border-top: 1px solid rgba(0, 0, 0, 0.1);
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}
.tab-bar:hover .tab {
  opacity: 1;
}
.tab-bar .tab {
  -webkit-transition: all 150ms ease;
  opacity: .8;
  position: relative;
  background: #31343a;
  top: 0;
  max-width: 160px;
  line-height: 28px;
  padding: 0 10px;
  color: #d6d6d6;
  height: 28px;
  font-size: 12px;
}
.tab-bar .tab:first-child {
  padding-left: 10px;
  margin-left: 0 !important;
}
.tab-bar .tab:last-child {
  box-shadow: 2px 0 18px rgba(0, 0, 0, 0.1);
}
.tab-bar .tab .close-icon {
  right: 10px;
  z-index: 3;
  text-align: right;
  line-height: 28px;
  color: #d6d6d6;
}
.tab-bar .tab .close-icon:hover {
  color: inherit;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  right: 10px;
  top: 11px;
  width: 8px;
  height: 8px;
  border-color: #ffffff;
}
.tab-bar .tab.modified:hover .close-icon:hover {
  color: #ffffff;
}
.tab-bar .tab .title {
  position: relative;
  z-index: 1;
  padding-right: 15px;
  opacity: .6;
}
.tab-bar .tab .title:before {
  padding-right: 3px;
  color: #555555 !important;
  text-shadow: none;
}
.tab-bar .tab:first-child {
  margin-left: 20px;
}
.tab-bar .tab.active {
  z-index: 1;
  background: #4d5154;
  color: #ffffff;
  font-weight: normal;
  box-shadow: none;
}
.tab-bar .tab.active .close-icon {
  line-height: 27px;
  color: #d6d6d6;
}
.tab-bar .tab.active .title {
  opacity: 1;
}
.tab-bar .tab.active .title:before {
  color: #62a4dc !important;
  text-shadow: 2px 2px 0 rgba(0, 0, 0, 0.1);
}
.tab-bar .tab:not(.active):hover {
  background-color: #383c42;
}
.tab-bar .tab.active:hover .close-icon {
  color: #d6d6d6;
}
.tab-bar .tab.active:hover .close-icon:hover {
  color: inherit;
}
.tab-bar .placeholder {
  height: 28px;
}
.tab-bar .placeholder:after {
  top: 28px;
}
