css:
.tree-view,
.tree-view-scroller {
  color: #aaaaaa;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  background-color: #2b2e31;
}
atom-workspace .tree-view-resizer {
  padding-top: 40px;
  background-color: #2b2e31;
  border-right: 1px solid #242729;
}
atom-workspace .tree-view {
  padding: 0;
}
atom-workspace .tree-view > .directory > .header {
  background-color: #3a3e42;
  line-height: 32px;
  height: 40px;
  padding-top: 8px;
  padding-left: 8px;
  position: fixed;
  top: 32px;
  width: 100%;
  z-index: 1001;
  margin-top: -32px;
  color: #ffffff;
}
atom-workspace .tree-view > .directory > .header .name {
  font-weight: 400;
}
atom-workspace .tree-view .directory {
  color: #aaaaaa;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
atom-workspace .tree-view .directory.status-added {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #26c26a;
}
atom-workspace .tree-view .directory.status-ignored {
  color: #6d6d6d;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
atom-workspace .tree-view .directory.status-modified {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #5293d8;
}
atom-workspace .tree-view .directory.status-removed {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #cc0000;
}
atom-workspace .tree-view .directory.status-renamed {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #ff982d;
}
atom-workspace .tree-view .directory.selected:before {
  background-color: #4182c4;
}
atom-workspace .tree-view .header.list-item {
  transition: color 200ms linear, background 200ms linear;
}
atom-workspace .tree-view .header.list-item:hover {
  color: #ffffff;
}
atom-workspace .tree-view .entry.list-item {
  color: #aaaaaa;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  transition: color 0.2s;
}
atom-workspace .tree-view .entry.list-item.status-added {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #26c26a;
}
atom-workspace .tree-view .entry.list-item.status-ignored {
  color: #6d6d6d;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
atom-workspace .tree-view .entry.list-item.status-modified {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #5293d8;
}
atom-workspace .tree-view .entry.list-item.status-removed {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #cc0000;
}
atom-workspace .tree-view .entry.list-item.status-renamed {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  color: #ff982d;
}
atom-workspace .tree-view .entry.list-item > span,
atom-workspace .tree-view .entry.list-item > .name {
  position: relative;
  z-index: 2001;
}
atom-workspace .tree-view .entry.list-item:before {
  transition: color 200ms linear, background 200ms linear;
  content: '';
  position: absolute;
  left: -100%;
  right: 0;
  height: 24px !important;
  width: 200%;
  z-index: 1003;
}
atom-workspace .tree-view .entry.list-item:hover {
  color: #ffffff;
}
atom-workspace .tree-view .entry.list-item:hover:before {
  background-color: #484d52;
  left: -100%;
  right: 0;
  height: 24px !important;
  width: 200%;
}
atom-workspace .tree-view .entry.list-item.selected {
  color: #ffffff;
}
atom-workspace .tree-view .entry.list-item.selected:before {
  background-color: #4182c4;
}
.focusable-panel {
  opacity: 1;
}
.focusable-panel:focus .selected > .name,
.focusable-panel:focus .selected > .name:before,
.focusable-panel:focus .selected > .list-item > .name,
.focusable-panel:focus .selected > .list-item > .name:before {
  color: #ffffff !important;
}
