css:
.settings-view .package-readme {
  font-size: 1.25em;
}
.settings-view .package-readme h1 {
  font-size: 2em;
  margin-top: 1.5em;
}
.settings-view .package-readme h2 {
  font-size: 1.75em;
  margin-top: 1.5em;
}
.settings-view .package-readme h3 {
  font-size: 1.5em;
  margin-top: 1.75em;
}
.settings-view .package-readme h4 {
  font-size: 1.25em;
  margin-top: 1.25em;
}
.settings-view .package-readme h1,
.settings-view .package-readme h2,
.settings-view .package-readme h3,
.settings-view .package-readme h4 {
  color: #e0e8e8;
}
.settings-view .package-readme a {
  color: #45d9df;
}
.settings-view .package-readme hr {
  border-color: #848474;
}
.settings-view .package-readme blockquote {
  border-left-color: #848474;
}
.settings-view .package-readme pre {
  margin-bottom: 1em;
}
.settings-view .package-readme img {
  max-width: 100%;
}
.settings-view .package-readme table {
  display: block;
  margin-bottom: 1em;
  width: 100%;
  overflow: auto;
  word-break: normal;
}
.settings-view .package-readme table th {
  font-weight: bold;
  text-align: center;
}
.settings-view .package-readme table th,
.settings-view .package-readme table td {
  padding: 6px 13px;
  border: 1px solid #464949;
}
.settings-view .package-readme table tr {
  border-top: 1px solid #464949;
  background-color: #2d2e2e;
}
.settings-view .package-readme table tr:nth-child(2n) {
  background-color: #54544d;
}
.settings-view .package-readme kbd {
  display: inline-block;
  padding: .3em .4em;
  font-size: .8em;
  line-height: 1;
  color: #e0e8e8;
  vertical-align: middle;
  background-color: #464848;
  border: solid 1px #464949;
  border-bottom-color: #2d2f2f;
  border-radius: 2px;
  box-shadow: inset 0 -1px 0 #2d2f2f;
}
