css:
.welcome {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 4em;
  font-size: 1.25em;
  line-height: 1.4;
  color: #b0b6b6;
  background-color: #2d2e2e;
  overflow-x: auto;
}
.welcome p {
  line-height: inherit;
}
.welcome a {
  color: #45d9df;
}
.welcome ul {
  padding-left: 2em;
}
.welcome-container {
  width: 100%;
  max-width: 400px;
}
.welcome-header {
  margin: 0 0 6em 0;
}
.welcome-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 2em auto;
  color: #e0e8e8;
}
.welcome-title {
  font-size: 1.4em;
  text-align: center;
  line-height: 1.3;
  margin: 1em 0;
}
.welcome-card {
  margin: 1em 0;
  border-radius: 4px;
  border: 1px solid #464949;
  background-color: #353636;
}
.welcome-summary {
  padding: 1em 1.5em;
  font-size: 1.1em;
  font-weight: 300;
  line-height: 1.2;
  cursor: pointer;
}
.welcome-summary:hover {
  color: #bbc0c0;
}
.welcome-summary::-webkit-details-marker {
  display: none;
}
.welcome-summary:before {
  width: 20px;
  color: #848474;
}
details[open] .welcome-summary:before {
  color: #e0e8e8;
}
details[open] .welcome-summary {
  color: #e0e8e8;
}
.welcome-highlight {
  color: #e0e8e8;
}
.welcome-detail {
  border-top: 1px solid #464949;
  padding: 1.5em;
}
.welcome-detail .welcome-note {
  margin-bottom: 0;
}
.welcome-img {
  display: block;
  max-width: 100%;
  border-radius: 3px;
  margin: 0 0 1em 0;
  border: 1px solid #464949;
}
.welcome-key {
  display: inline-block;
  padding: .3em .4em;
  font-size: .8em;
  line-height: 1;
  border-radius: 3px;
  color: #e0e8e8;
  border: 1px solid #848474;
  background: rgba(0, 0, 0, 0.1);
}
.welcome-note {
  font-size: .86em;
  color: #848474;
}
.welcome-metrics {
  margin-top: 2em;
}
.welcome-footer {
  text-align: center;
}
