.tab-bar .tab .close-icon {
  padding-left: 4px;
  right: 16px;
  width: 20px;
  height: 16px;
  z-index: 3;
  padding: 0 .5rem;
  text-align: center;
  line-height: 3rem;
  color: transparent;
  transition: all 100ms ease;
}
.tab-bar .tab .close-icon::before {
  font-size: 16px;
  width: 24px;
  height: 16px;
  position: absolute;
  top: 12px;
  right: -8px;
  padding-left: 8px;
  background: linear-gradient(to right, transparent, #ced5db 30%);
}
.tab-bar .tab .close-icon:hover {
  color: inherit;
}
.tab-bar .tab .close-icon:active {
  opacity: .6;
}
.tab-bar .tab.active {
  z-index: 1;
  flex: 2 0 auto;
  background: #455463;
  color: #fafafa;
}
