.tab-bar {
  height: 26px;
  background: #d8d8d8;
  box-shadow: inset 0 -1px 0px 4px rgba(0, 0, 0, 0.1);
  padding: 0px;
  box-sizing: border-box;
}
.tab-bar:after {
  content: "";
  position: absolute;
  bottom: 0;
  height: 4px;
  left: 0px;
  width: 100%;
  background-color: #f0f0f0;
  border-top: 1px solid #cdcdcd;
  border-bottom: 1px solid rgba(0, 0, 0, 0.2);
}
.tab-bar .tab {
  position: relative;
  top: 0px;
  height: 22px;
  line-height: 20px;
  color: #444444;
  margin-left: -1px;
  border-top-right-radius: 0px;
  transition: color 0.1s ease-in;
  box-shadow: 4px 0px 4px rgba(0, 0, 0, 0.1);
