.tab-bar {
  height: 34px;
  background: #363636;
  box-shadow: inset 0 -8px 8px -4px rgba(0, 0, 0, 0.15);
  padding: 0 10px 0 10px;
  box-sizing: border-box;
}
.tab-bar:after {
  content: "";
  position: absolute;
  bottom: 0;
  height: 5px;
  left: 0px;
  width: 100%;
  background-color: #434343;
  border-top: 1px solid #575757;
  border-bottom: 1px solid #3C3C3C;
}
.tab-bar .tab {
  position: relative;
  top: 5px;
  height: 24px;
  line-height: 24px;
  color: #aaaaaa;
  padding-left: 0;
  margin-left: 19px;
  border-top-right-radius: 0px;
  box-shadow: inset -1px -1px 1px rgba(0, 0, 0, 0.05);
  transition: color 0.1s ease-in;
  box-shadow: inset -1px 1px 0 #484848, 4px 0px 4px rgba(0, 0, 0, 0.1);
}
.tab-bar .tab,
.tab-bar .tab:before {
  background-image: -webkit-linear-gradient(top, #444444, #3d3d3d);
}
.tab-bar .tab:before {
  content: '';
  position: absolute;
  top: 0;
  left: -18px;
  height: 24px;
  width: 40px;
  border-top-left-radius: 0px;
  box-shadow: inset 1px 1px 0 #484848, -4px 0px 20px rgba(0, 0, 0, 0.1);
  border: none;
}
.tab-bar .tab .close-icon {
  z-index: 3;
  line-height: 24px;
  -webkit-transform: skewX(0deg);
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 9px;
  right: 14px;
  width: 8px;
  height: 8px;
}
.tab-bar .tab .title {
  -webkit-transform: skewX(0deg);
}
.tab-bar .tab:first-child {
  margin-left: 20px;
}
.tab-bar .tab.active {
  z-index: 10;
  line-height: 21px;
  color: #ffffff;
  box-shadow: inset -1px 1px 0 #484848, 4px -4px 4px rgba(0, 0, 0, 0.1);
  border: none;
}
.tab-bar .tab.active .title {
  position: relative;
  top: 1px;
}
.tab-bar .tab.active .close-icon {
  line-height: 23px;
}
.tab-bar .tab.active,
.tab-bar .tab.active:before {
  background-image: -webkit-linear-gradient(top, #555555, #434343);
  height: 25px;
}
.tab-bar .tab.active:before {
  border: none;
  box-shadow: inset 1px 1px 0 #484848, -4px -4px 4px rgba(0, 0, 0, 0.1);
}
.tab-bar .tab:hover {
  color: #ffffff;
}
.tab-bar .tab.active:hover .close-icon {
  color: #ffffff;
}
