}
.highlight {
  color: #ffffff;
  font-weight: bold;
  text-shadow: none;
  background-color: rgba(255, 255, 255, 0.07);
  border-radius: 2px;
  padding: 1px 3px;
}
.highlight-info {
  color: #cfe1f4;
  font-weight: bold;
  text-shadow: none;
