.tooltip {
  white-space: nowrap;
}
.tooltip .keystroke {
  font-family: Helvetica, Arial, sans-serif;
  font-size: 13px;
  color: #777;
  padding-left: 2px;
}
.tooltip.in {
  opacity: 1;
}
.tooltip .tooltip-inner {
  line-height: 19px;
  border-radius: 2px;
  background-color: #ffffff;
  color: #333333;
  white-space: nowrap;
  max-width: none;
}
.tooltip.top .tooltip-arrow {
  border-top-color: #ffffff;
}
.tooltip.top-left .tooltip-arrow {
  border-top-color: #ffffff;
}
.tooltip.top-right .tooltip-arrow {
  border-top-color: #ffffff;
}
.tooltip.right .tooltip-arrow {
  border-right-color: #ffffff;
}
.tooltip.left .tooltip-arrow {
  border-left-color: #ffffff;
}
.tooltip.bottom .tooltip-arrow {
  border-bottom-color: #ffffff;
}
.tooltip.bottom-left .tooltip-arrow {
  border-bottom-color: #ffffff;
}
.tooltip.bottom-right .tooltip-arrow {
  border-bottom-color: #ffffff;
}
.tree-view {
  transition: all .2s ease;
  font-size: 11px;
  border-right: 1px solid black;
  background: #1e1e1e;
}
.tree-view .selected:before {
  background: #444;
  box-shadow: inset -3px 0 0 rgba(0, 0, 0, 0.05);
}
.focusable-panel {
  transition: all .2s ease;
  opacity: 1;
  background: #1e1e1e;
  box-shadow: inset -3px 0 0 rgba(0, 0, 0, 0.05);
}
.focusable-panel:focus {
  background: #262626;
}
.focusable-panel:focus .selected:before {
  background: linear-gradient(#4e4e4e, #434343);
}
.focusable-panel:focus .selected > .name,
.focusable-panel:focus .selected > .name:before,
.focusable-panel:focus .selected > .list-item > .name,
.focusable-panel:focus .selected > .list-item > .name:before {
  color: #fff !important;
}
.key-binding {
  background: -webkit-linear-gradient(rgba(100, 100, 100, 0.5), rgba(70, 70, 70, 0.5));
  -webkit-box-shadow: inset 1px 1px 0 rgba(255, 255, 255, 0.1);
  display: inline-block;
  line-height: 100%;
  border-radius: 2px;
  margin-left: 5px;
  font-family: Helvetica, Arial, sans-serif;
  font-size: 12px;
  padding: 5px;
}
.badge {
  font-weight: normal;
  color: #ffffff;
  text-shadow: 0 1px 0 rgba(0, 0, 0, 0.5);
  background: rgba(255, 255, 255, 0.07);
}
.description {
  font-size: 12px;
}
