css:
* {
  box-sizing: border-box;
}
body {
  background-color: #2b2e31;
  color: #6d6d6d;
}
atom-workspace {
  background-color: transparent;
}
status-bar {
  background-color: #3a3e42;
  height: 32px;
  line-height: 32px;
}
.settings-view .editor-container atom-text-editor[mini],
.settings-view .editor-container atom-text-editor[mini]::shadow,
.settings-view .editor-container atom-text-editor[mini].is-focused,
.settings-view .editor-container atom-text-editor[mini].is-focused::shadow {
  color: #333333;
  background-color: #fff;
}
.background-tips li {
  color: #6d6d6d;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  text-shadow: 0 2px #1d1f21;
  font-size: 0.5em;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-track,
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-corner {
  transition: background 800ms linear;
  background: transparent;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-track:hover,
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-corner:hover {
  background: rgba(0, 0, 0, 0.2);
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-thumb {
  background: rgba(0, 0, 0, 0.4);
  border-radius: 4px;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-thumb:hover {
  background: #484d52;
}
