css:
.tab-bar {
  height: 40px;
  background: #3a3e42;
  z-index: 1002;
}
.tab-bar .tab {
  transition: all 200ms linear;
  color: #aaaaaa;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  height: 32px;
  line-height: 32px;
  margin-top: 8px;
  background: #3a3e42;
}
.tab-bar .tab .title::before {
  color: #aaaaaa !important;
}
.tab-bar .tab:first-child {
  padding-left: 8px;
}
.tab-bar .tab:hover {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
}
.tab-bar .tab:hover .close-icon {
  display: block;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  color: #5293d8;
  border-color: #5293d8;
  display: block;
  right: 12px;
  top: 12px;
  width: 8px;
  height: 8px;
  border-width: 1px;
}
.tab-bar .tab .close-icon {
  transition: color 200ms linear, background 200ms linear;
  color: #aaaaaa;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  position: absolute;
  top: 9px;
  right: 8px;
  height: 16px;
  width: 16px;
  border-radius: 2px;
  display: none;
}
.tab-bar .tab .close-icon:before {
  content: 'âœ•';
  position: absolute;
  top: 2px;
  right: 1px;
  width: 12px;
  height: 12px;
}
.tab-bar .tab .close-icon:hover {
  color: #ffffff;
  background-color: #2b2e31;
}
.tab-bar .tab.active {
  color: #ffffff;
  font-size: 13px;
  font-weight: 300;
  letter-spacing: 0.04em;
  margin-left: 8px;
  background: #1d1f21;
  border-top-right-radius: 4px;
  border-top-left-radius: 4px;
}
.tab-bar .tab.active .title::before {
  color: #ffffff;
}
.tab-bar .tab.active .close-icon {
  display: block;
}
.tab-bar .tab.active .close-icon:hover {
  color: #ffffff;
  background-color: #3a3e42;
}
