css:
/*
* This is sortof prepared for adjustable themes.
* Everything is based of a single color taken from the syntax theme.
*/
.emmet-panel {
  position: absolute;
  z-index: 10;
  background: #ccd4d4;
  color: #4a4f4f;
  padding: 5px;
  min-width: 200px;
  font-size: 12px;
  -webkit-box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
}
.emmet-panel-tail {
  position: absolute;
  left: 8px;
  margin-top: -6px;
  width: 15px;
  height: 15px;
  content: '';
  display: inline-block;
  background: inherit;
  z-index: -1;
  -webkit-transform: rotate(45deg);
  transform: rotate(45deg);
}
.emmet-panel .atom-text-editor[mini] {
  z-index: 1;
  line-height: 16px;
}
.emmet-prompt {
  min-width: -webkit-min-content;
  padding: 10px;
  /* &__label {
		display: inline-block;
		white-space: nowrap;
		min-width: -webkit-min-content;
		min-width: min-content;
		padding-right: 10px;
		margin: 0;
	} */
}
.emmet-prompt__input {
  flex: 1 100%;
  margin: 0;
}
