css:
/*
* This is sortof prepared for adjustable themes.
* Everything is based of a single color taken from the syntax theme.
*/
.highlight.linter-info .region {
  background-color: #1866aa;
  -webkit-mask-image: url(atom://linter/images/wave.svg);
  -webkit-mask-repeat: repeat;
  -webkit-mask-size: .9em 1.3em;
  -webkit-mask-position: bottom left;
}
.highlight.linter-warning .region {
  background-color: #8d6435;
  -webkit-mask-image: url(atom://linter/images/wave.svg);
  -webkit-mask-repeat: repeat;
  -webkit-mask-size: .9em 1.3em;
  -webkit-mask-position: bottom left;
}
.highlight.linter-error .region {
  background-color: #942e2e;
  -webkit-mask-image: url(atom://linter/images/wave.svg);
  -webkit-mask-repeat: repeat;
  -webkit-mask-size: .9em 1.3em;
  -webkit-mask-position: bottom left;
}
