css:
/*
* This is sortof prepared for adjustable themes.
* Everything is based of a single color taken from the syntax theme.
*/
.welcome {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 4em;
  font-size: 1.25em;
  line-height: 1.4;
  color: #4a4f4f;
  background-color: #d5dbdb;
  overflow-x: auto;
}
.welcome p {
  line-height: inherit;
}
.welcome a {
  color: #3368ca;
}
.welcome ul {
  padding-left: 2em;
}
.welcome-container {
  width: 100%;
  max-width: 400px;
}
.welcome-header {
  margin: 0 0 6em 0;
}
.welcome-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 2em auto;
  color: #172626;
}
.welcome-title {
  font-size: 1.4em;
  text-align: center;
  line-height: 1.3;
  margin: 1em 0;
}
.welcome-card {
  margin: 1em 0;
  border-radius: 4px;
  border: 1px solid #c4cdcd;
  background-color: #dde2e2;
}
.welcome-summary {
  padding: 1em 1.5em;
  font-size: 1.1em;
  font-weight: 300;
  line-height: 1.2;
  cursor: pointer;
}
.welcome-summary:hover {
  color: #545959;
}
.welcome-summary::-webkit-details-marker {
  display: none;
}
.welcome-summary:before {
  width: 20px;
  color: rgba(74, 79, 79, 0.82);
}
details[open] .welcome-summary:before {
  color: #172626;
}
details[open] .welcome-summary {
  color: #172626;
}
.welcome-highlight {
  color: #172626;
}
.welcome-detail {
  border-top: 1px solid #c4cdcd;
  padding: 1.5em;
}
.welcome-detail .welcome-note {
  margin-bottom: 0;
}
.welcome-img {
  display: block;
  max-width: 100%;
  border-radius: 3px;
  margin: 0 0 1em 0;
  border: 1px solid #c4cdcd;
}
.welcome-key {
  display: inline-block;
  padding: .3em .4em;
  font-size: .8em;
  line-height: 1;
  border-radius: 3px;
  color: #172626;
  border: 1px solid rgba(74, 79, 79, 0.82);
  background: rgba(0, 0, 0, 0.1);
}
.welcome-note {
  font-size: .86em;
  color: rgba(74, 79, 79, 0.82);
}
.welcome-metrics {
  margin-top: 2em;
}
.welcome-footer {
  text-align: center;
}
