[isotope-ui-bg-gradient='true'] atom-workspace {
  background-image: linear-gradient(#45454a 0%, #252828 100%);
}
[isotope-ui-bg-gradient='true'] atom-workspace:before {
  content: '';
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background-image: url(data:image/png;base64,);
  opacity: .20;
}
[isotope-ui-bg-image='true'] {
  background-size: cover;
  background-repeat: no-repeat;
  background-image: url(atom://isotope-ui/resources/images/hubble.jpg);
}
