/*
Thanks to: http://stephanwagner.me/only-css-loading-spinner
*/
@keyframes spinner {
  to {
    transform: rotate(360deg);
  }
}
@-webkit-keyframes spinner {
  to {
    -webkit-transform: rotate(360deg);
  }
}
.loading-spinner-large {
  min-width: 42px;
  min-height: 42px;
  display: block;
  position: relative;
}
.loading-spinner-large:before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 42px;
  height: 42px;
  margin-top: -21px;
  margin-left: -21px;
  border-radius: 50%;
  border: 2px solid #8b9b9b;
  border-top-color: #cef3f3;
  animation: spinner 0.7s cubic-bezier(0.645, 0.045, 0.355, 1) infinite;
  -webkit-animation: spinner 0.7s cubic-bezier(0.645, 0.045, 0.355, 1) infinite;
}
.loading-spinner-large.inline-block {
  display: inline-block;
  min-height: 1em;
}
.loading-spinner-medium {
  min-width: 32px;
  min-height: 32px;
