.btn-group > .btn {
  border: none;
}
.editor.mini {
  color: #444444;
  background: #ffffff;
  border: 1px solid rgba(204, 204, 204, 0.9);
  border-radius: 0;
  padding-left: 5px;
  font-family: 'Segoe UI', 'Lucida Grande', sans-serif;
  font-size: 12px;
}
.editor.mini .cursor {
  border-color: #000;
}
.editor.mini .selection .region {
  background: rgba(0, 0, 0, 0.2);
}
.editor.editor-colors.mini.is-focused {
  box-shadow: 0 0 5px #338EDA;
  border: 1px solid #338EDA;
  background: #ffffff;
}
.editor.editor-colors.mini.is-focused .selection .region {
  background: #99d6ff;
}
@-webkit-keyframes highlight {
  from {
    background: rgba(100, 255, 100, 0.7);
  }
  to {
    background: null;
  }
}
.editor .highlighted.selection .region {
  -webkit-animation-name: highlight;
  -webkit-animation-duration: 1s;
  -webkit-animation-iteration-count: 1;
}
