  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 14px;
  width: 14px;
  height: 14px;
  content: "\f03a";
}
.select-list ol.list-group.mark-active li.active:before,
.select-list ol.list-group.mark-active li.active:before {
