}
.select-list.popover-list {
  background: #e0e0e0;
  box-shadow: 0px 2px 5px #9f9f9f;
  padding: 5px;
  border-radius: 0;
  border: 1px solid #bfbfbf;
}
.select-list.popover-list .selected {
  border: 1px solid #70c0e7;
