}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 12px;
  width: 8px;
  height: 8px;
}
.tab-bar .tab:hover:not(.active) {
  background: -webkit-linear-gradient(top, #f8f8f8 0%, #eeeeee 100%);
}
.tab-bar .tab:hover .close-icon {
  display: block;
}
.tab-bar .tab.active {
