css:
/*
* This is sortof prepared for adjustable themes.
* Everything is based of a single color taken from the syntax theme.
*/
.linter-statusbar {
  max-height: 140px;
  overflow-y: scroll;
  margin-bottom: 0;
}
.linter-statusbar dd:before,
.linter-statusbar dd:after {
  content: " ";
  display: table;
}
.linter-statusbar dd:after {
  clear: both;
}
.linter-statusbar dt {
  float: left;
  width: 70px;
  clear: left;
  text-align: right;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  font-weight: normal !important;
  padding: 0.5px 0 0.5px 0;
}
.linter-statusbar dd {
  padding-left: 25px;
  margin-left: 50px;
}
.linter-statusbar dd > span {
  cursor: pointer;
}
.linter-statusbar dd > span:hover {
  color: #171d26;
}
.linter-statusbar dd .pos {
  color: rgba(255, 255, 255, 0.2);
  padding: 2px;
  border-radius: 1px 2px;
}
.linter-statusbar dd .copy:before {
  font-size: 14px;
  width: 14px;
  height: 14px;
}
.linter-statusbar dd .message-highlighted {
  background-color: #2e3239;
}
