css:
/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
.editor-colors,
:host {
  background-color: #f7f4ef;
  color: #2b3f6a;
}
atom-text-editor,
atom-text-editor::shadow,
:host {
  background-color: #f7f4ef;
  color: #2b3f6a;
}
atom-text-editor .wrap-guide,
atom-text-editor::shadow .wrap-guide,
:host .wrap-guide {
  background-color: #cac1ba;
}
atom-text-editor .indent-guide,
atom-text-editor::shadow .indent-guide,
:host .indent-guide {
  color: #a9c1d6;
}
atom-text-editor .invisible-character,
atom-text-editor::shadow .invisible-character,
:host .invisible-character {
  color: #a9c1d6;
}
atom-text-editor .gutter,
atom-text-editor::shadow .gutter,
:host .gutter {
  background-color: #cac1ba;
  color: #2b140b;
  margin-right: 0.75em;
}
atom-text-editor .gutter .line-number.cursor-line,
atom-text-editor::shadow .gutter .line-number.cursor-line,
:host .gutter .line-number.cursor-line {
  background-color: #a99a8f;
  color: #2b140b;
}
atom-text-editor .gutter .line-number.cursor-line-no-selection,
atom-text-editor::shadow .gutter .line-number.cursor-line-no-selection,
:host .gutter .line-number.cursor-line-no-selection {
  color: #2b140b;
}
atom-text-editor .line-number.folded,
atom-text-editor::shadow .line-number.folded,
:host .line-number.folded,
atom-text-editor .line-number:after,
atom-text-editor::shadow .line-number:after,
:host .line-number:after,
atom-text-editor .fold-marker:after,
atom-text-editor::shadow .fold-marker:after,
:host .fold-marker:after {
  color: #255f93;
}
atom-text-editor .selection .region,
atom-text-editor::shadow .selection .region,
:host .selection .region {
  background-color: #ded1bc;
}
.bracket-matcher {
  background-color: rgba(237, 169, 251, 0.61);
}
.comment {
  color: #9ca3b0;
}
.entity.name.type {
  color: #379566;
}
.entity.other.inherited-class {
  color: #919a2f;
}
.keyword {
  color: #919a2f;
}
.keyword.control {
  color: #2b3f6a;
  font-weight: 600;
}
.keyword.operator {
  font-weight: bold;
  color: #1c4b33;
}
.keyword.other.special-method {
  color: #5e78e1;
}
.keyword.other.attribute-name {
  color: #2b3f6a;
  font-weight: bold;
}
.keyword.other.unit {
  color: #919a2f;
}
.storage {
  color: #5e78e1;
}
.storage.todo {
  color: #8100c6;
  background-color: rgba(255, 255, 0, 0.06);
}
.constant {
  color: #c16161;
}
.constant.character.escape {
  color: #919a2f;
}
.constant.numeric {
  color: #c16161;
}
.constant.other.color {
  color: #919a2f;
}
.constant.other.symbol {
  color: #919a2f;
}
.variable {
  color: #5e78e1;
}
.variable.interpolation {
  color: #3354d9;
}
.invalid.illegal {
  background-color: #c16161;
  color: #f7f4ef;
}
.string.interpolated {
  background-color: rgba(255, 204, 0, 0.03);
}
.string.quoted {
  background-color: rgba(255, 204, 0, 0.03);
  color: #794b16;
}
.string.regexp {
  color: #379566;
}
.string.regexp .source.ruby.embedded {
  color: #d08e25;
}
.string.other.link {
  color: #c16161;
}
.punctuation.definition.comment {
  color: #808999;
}
.punctuation.definition.tag {
  color: #ac4343;
}
.punctuation.definition.heading,
.punctuation.definition.identity {
  color: #5e78e1;
}
.punctuation.definition.bold {
  color: bold-color;
  font-weight: bold;
}
.punctuation.section.embedded {
  color: #ac4343;
}
.support.class {
  color: #379566;
}
.support.function {
  color: #d08e25;
}
.entity.name.function {
  color: #d08e25;
}
.entity.name.class,
.entity.name.type.class {
  color: #2b3f6a;
}
.entity.name.section {
  color: #c16161;
}
.entity.name.tag {
  color: #5e78e1;
}
.entity.name.label {
  color: #5e78e1;
}
.entity.other.attribute-name {
  color: #d08e25;
}
.entity.other.attribute-name.id {
  color: #5e78e1;
}
.meta.class {
  color: #2b3f6a;
}
.meta.method {
  color: #255f93;
}
.meta.link {
  color: #d08e25;
}
.meta.require {
  color: #5e78e1;
}
.meta.selector {
  color: #d08e25;
}
.meta.separator {
  color: #2b3f6a;
}
.none {
  color: #2b3f6a;
}
atom-text-editor[data-grammar="source gfm"]:not(.mini) .gutter,
atom-text-editor[data-grammar="source gfm"]:not(.mini)::shadow .gutter,
atom-text-editor[data-grammar="source gfm"]:not(.mini) .scroll-view,
atom-text-editor[data-grammar="source gfm"]:not(.mini)::shadow .scroll-view {
  padding-top: 0.5em;
}
atom-text-editor[data-grammar="source gfm"]:not(.mini) .gutter .line-number-0,
atom-text-editor[data-grammar="source gfm"]:not(.mini)::shadow .gutter .line-number-0,
atom-text-editor[data-grammar="source gfm"]:not(.mini) .scroll-view .line-number-0,
atom-text-editor[data-grammar="source gfm"]:not(.mini)::shadow .scroll-view .line-number-0 {
  padding-top: 0.5em;
  top: -0.5em !important;
}
/*---------------------------------------
 below: optional extra stuff for addon packages
-----------------------------------------*/
.highlights .highlight-selected .region {
  border-radius: 4px;
  box-sizing: content-box;
  padding: 0 1px 0 0;
  margin: -1px 0 0 -1px;
  background-color: transparent;
  border-width: 1.5px;
  border-style: dashed;
}
.highlights .highlight-selected.light-theme .region {
  border-color: rgba(238, 11, 143, 0.8);
}
.highlights .highlight-selected.light-theme.background .region {
  background-color: rgba(236, 13, 132, 0.35);
}
atom-text-editor[data-grammar="source gfm"]::shadow .gutter {
  margin-right: 2em;
}
.markup.changed {
  color: #8100c6;
}
.markup.deleted {
  color: #c16161;
}
.markup.italic {
  color: #087bac;
  font-style: italic;
}
.markup.bold {
  color: #06587b;
  font-weight: bold;
}
.markup.heading .punctuation.definition.heading {
  color: #5e78e1;
}
.markup.inserted {
  color: #919a2f;
}
.markup.list {
  color: #e24129;
  font-weight: bold;
}
.markup.raw {
  background-color: rgba(255, 255, 255, 0.65);
  color: #2b140b;
}
.markup.raw.inline {
  color: #919a2f;
}
.markup.strike {
  text-decoration: line-through;
}
.markup.underline {
  text-decoration: underline;
}
.markup.heading {
  color: #2b140b;
  font-weight: normal;
  position: relative;
  left: 0px;
}
.markup.heading.heading-1 {
  font-size: 145%;
  top: -8px;
}
.markup.heading.heading-2 {
  font-size: 130%;
  top: -6px;
}
.markup.heading.heading-3 {
  font-size: 115%;
  top: -3px;
}
.source.pfm .markup.math {
  color: green;
}
.quote {
  color: #794b16;
  font-style: italic;
}
.source.gfm .markup.heading {
  font-weight: normal;
}
.gfm.entity {
  color: #255f93;
}
.gfm.username {
  color: #aa5c08;
}
.gfm.mention {
  color: rgba(205, 118, 42, 0.43);
}
.gfm.list {
  color: #e24129;
  font-weight: bold;
}
