css:
.text-subtle {
  color: rgba(255, 255, 255, 0.5);
}
.event.selected {
  background-color: #1f1f1f !important;
}
.btn:hover,
.btn:focus:not(.btn-default) {
  color: rgba(255, 255, 255, 0.8);
}
.btn.active,
.btn:active {
  -webkit-box-shadow: none;
  box-shadow: none;
  color: #dcdcdc;
  background-color: #353535;
}
.btn.selected {
  color: #dcdcdc;
  background-color: #424242;
}
.btn.selected:hover {
  background-color: #494949;
}
.btn.btn-primary:focus {
  background-color: #dcdcdc;
  color: #282828;
}
.btn.btn-primary.disabled,
.btn.btn-primary[disabled] {
  background-color: #f6f6f6;
  color: #282828;
}
.editor.mini {
  color: #fff;
  background-color: #282828;
  border-radius: 0;
  padding-left: 5px;
}
.editor.mini .selection .region {
  background-color: #3a3a3a;
}
.editor.editor-colors.mini.is-focused {
  background-color: #252525;
}
.editor.editor-colors.mini.is-focused .selection .region {
  background-color: #4c4c4c;
}
.editor .gutter.drop-shadow {
  -webkit-box-shadow: -2px 0 10px 2px #222222;
}
@-webkit-keyframes highlight {
  from {
    background-color: #3a3a3a;
  }
  to {
    background-color: null;
  }
}
.editor .highlighted.selection .region {
  -webkit-animation-name: highlight;
  -webkit-animation-duration: 1s;
  -webkit-animation-iteration-count: 1;
}
.editor .wrap-guide,
.editor.react .wrap-guide {
  background-color: #5a5a5a;
}
.editor .scroll-view,
.editor.react .scroll-view {
  padding-left: 0;
}
.editor .gutter,
.editor.react .gutter {
  color: #666;
  background-color: #282828;
  border-right: none;
}
.editor .gutter .cursor-line,
.editor.react .gutter .cursor-line {
  background-color: #232323;
  opacity: 1;
}
.editor .gutter .line-number,
.editor.react .gutter .line-number {
  text-indent: 0.5em;
  padding-right: 0.5em;
  opacity: 1;
}
.editor .gutter .line-number .icon-right,
.editor.react .gutter .line-number .icon-right {
  opacity: 1;
  padding: 0 2px 0 0;
}
.editor .gutter .line-number.foldable .icon-right:before,
.editor.react .gutter .line-number.foldable .icon-right:before {
  background: -webkit-image-set(url("atom://indico-ui/images/fold-open.png") 1x, url("atom://indico-ui/images/fold-open@2x.png") 2x);
  background-size: cover;
  content: ' ';
  height: 1.2em;
  width: 1.6em;
}
.editor .gutter .line-number.foldable.folded .icon-right:before,
.editor.react .gutter .line-number.foldable.folded .icon-right:before {
  background: -webkit-image-set(url("atom://indico-ui/images/fold-closed.png") 1x, url("atom://indico-ui/images/fold-closed@2x.png") 2x);
  background-size: cover;
  content: ' ';
  height: 1.2em;
  width: 1.6em;
}
.editor .gutter .gutter:hover .line-number.foldable .icon-right:hover,
.editor.react .gutter .gutter:hover .line-number.foldable .icon-right:hover {
  opacity: 0.8;
}
.editor .line.cursor-line:before,
.editor.react .line.cursor-line:before {
  content: ' ';
  position: absolute;
  left: 0;
  right: 0;
  z-index: -3;
  background-color: #252525;
}
.editor .cursor,
.editor.react .cursor {
  border-color: rgba(255, 255, 255, 0.8);
  border-left-width: 1px;
}
.editor-colors {
  background-color: #282828;
}
.list-group li:not(.list-nested-item).selected,
.list-tree li:not(.list-nested-item).selected,
.list-group li.list-nested-item.selected > .list-item,
.list-tree li.list-nested-item.selected > .list-item {
  color: #fff;
}
.list-group li:not(.list-nested-item).selected:before,
.list-tree li:not(.list-nested-item).selected:before,
.list-group li.list-nested-item.selected > .list-item:before,
.list-tree li.list-nested-item.selected > .list-item:before {
  background-color: #dcdcdc;
}
.select-list.popover-list {
  background-color: #414141;
  box-shadow: 0 0 10px transparent;
  padding: 3px;
  border-radius: 0;
  border: 1px solid transparent;
}
.select-list.popover-list .editor {
  margin-bottom: 3px;
}
.select-list.popover-list .list-group li {
  padding-left: 3px;
}
.select-list.popover-list .list-group li.selected {
  color: #fff;
  background: rgba(255, 255, 255, 0.8);
}
.select-list.popover-list .list-group li.selected:before {
  display: block;
}
.select-list.popover-list .list-group li.selected .status.icon {
  color: #dcdcdc;
}
.overlay {
  color: #777;
  background-color: #414141;
  padding: 6px;
  border: 1px solid transparent;
  box-shadow: 0 2px 10px transparent;
  border-radius: 0;
}
.overlay.from-top {
  top: 50px;
}
.overlay .editor.mini,
.overlay .editor.mini.is-focused {
  margin-bottom: 6px;
  background-color: #282828;
  padding: 6px;
}
.overlay .editor.mini .cursor,
.overlay .editor.mini.is-focused .cursor {
  border-left-width: 1px;
  border-color: rgba(255, 255, 255, 0.8);
}
.overlay .select-list ol.list-group,
.overlay.select-list ol.list-group {
  background-color: #282828;
}
.overlay .select-list ol.list-group li,
.overlay.select-list ol.list-group li {
  padding: 6px;
}
.overlay .select-list ol.list-group li.two-lines,
.overlay.select-list ol.list-group li.two-lines {
  padding: 3px 6px;
}
.overlay .select-list ol.list-group li .status.icon,
.overlay.select-list ol.list-group li .status.icon {
  float: right;
  margin-left: 5px;
}
.overlay .select-list ol.list-group li .status.icon:before,
.overlay.select-list ol.list-group li .status.icon:before {
  margin-right: 0;
}
.overlay .select-list ol.list-group li.selected,
.overlay.select-list ol.list-group li.selected {
  color: #fff;
  background: rgba(255, 255, 255, 0.8);
}
.overlay .select-list ol.list-group li.selected::before,
.overlay.select-list ol.list-group li.selected::before {
  background-color: transparent;
}
.overlay .select-list ol.list-group li.selected .status.icon,
.overlay.select-list ol.list-group li.selected .status.icon {
  color: #dcdcdc;
}
.panel.bordered {
  border: 1px solid transparent;
  border-radius: 0;
}
.tool-panel {
  font-weight: normal;
  color: rgba(255, 255, 255, 0.8);
  text-shadow: none;
  position: relative;
  background-color: #232323;
}
.inset-panel {
  font-weight: normal;
  color: rgba(255, 255, 255, 0.8);
  text-shadow: none;
  position: relative;
  background-color: #232323;
}
.pane-item .panel {
  border-color: rgba(255, 255, 255, 0.7);
}
.panes .pane {
  background-color: #282828;
}
.panes .pane-row > *:last-child {
  border-right: none;
}
.panes .pane-column > * {
  border-bottom: 1px solid #fff;
}
.panes .pane-column > *:last-child {
  border-bottom: none;
}
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}
::-webkit-scrollbar-track {
  background: none;
}
::-webkit-scrollbar-thumb {
  background-color: #555;
}
::-webkit-scrollbar-corner {
  background: transparent;
}
.settings-view .config-menu .nav > li.active > a {
  color: #dcdcdc;
}
.form-control:focus {
  border-color: #f6f6f6;
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(220, 220, 220, 0.6);
}
.status-bar {
  background-color: #1f1f1f;
  color: #8c8c8c;
}
.status-bar .icon.icon-package,
.status-bar .icon.icon.squirrel {
  cursor: pointer;
}
.status-bar .icon.icon-squirrel:before {
  font-size: 16px;
  width: 16px;
  height: 16px;
}
.status-bar .text-info {
  color: #dcdcdc;
}
.status-bar .text-info:hover {
  color: #c3c3c3;
}
.status-bar .release-notes-status {
  color: #dcdcdc;
}
.status-bar .file-info,
.status-bar .cursor-position {
  color: #777;
}
.status-bar a {
  color: #777;
  transition: color 100ms ease;
}
.status-bar a:hover {
  color: #f18260;
  text-decoration: none;
}
.tab-bar {
  height: 50px;
  line-height: 50px;
  background-color: #1f1f1f;
}
.tab-bar .tab {
  display: inline-block;
  height: 50px;
  padding-left: 15px;
  padding-right: 30px;
  min-width: 100px;
  max-width: 260px;
  background-color: #1f1f1f;
  color: rgba(255, 255, 255, 0.5);
  transition: background-color 100ms ease;
  border-right: 1px solid #282828;
}
.tab-bar .tab .title {
  opacity: rgba(255, 255, 255, 0.5);
}
.tab-bar .tab:first-child {
  padding-left: 15px;
}
.tab-bar .tab:last-child {
  border-right: none;
}
.tab-bar .tab.active {
  background-color: #282828;
}
.tab-bar .tab.active .title {
  color: rgba(255, 255, 255, 0.8);
}
.tab-bar .tab.active:hover {
  background-color: #282828;
}
.tab-bar .tab:hover {
  background-color: #333333;
}
.tab-bar .tab:hover .title {
  color: rgba(255, 255, 255, 0.8);
}
.tab-bar .tab .close-icon {
  height: 30px;
  margin-right: 8px;
  margin-top: 2px;
}
.tab-bar .tab.modified:not(:hover) .close-icon:before,
.tab-bar .tab .close-icon:before {
  content: ' ';
  background-image: -webkit-image-set(url("atom://indico-ui/images/tab-close.png") 1x, url("atom://indico-ui/images/tab-close@2x.png") 2x);
  background-position: 50% 50%;
  opacity: 0.25;
  display: inline-block;
  margin-top: 9px;
}
.tab-bar .tab .close-icon:hover:before,
.tab-bar .tab.modified:hover .close-icon:hover:before {
  background-image: -webkit-image-set(url("atom://indico-ui/images/tab-close-hover.png") 1x, url("atom://indico-ui/images/tab-close-hover@2x.png") 2x);
  opacity: 1;
}
.tab-bar .tab.modified:not(:hover) .close-icon,
.tab-bar .tab.modified:hover .close-icon {
  top: 1px;
  right: 10px;
  width: 12px;
  height: 30px;
  border: none;
  border-radius: 0;
}
.tab-bar .tab.modified:not(:hover) .close-icon:before,
.tab-bar .tab.modified:hover .close-icon:before {
  background-image: -webkit-image-set(url("atom://indico-ui/images/tab-dirty.png") 1x, url("atom://indico-ui/images/tab-dirty@2x.png") 2x);
  opacity: 1;
}
pre {
  box-shadow: none;
  color: rgba(255, 255, 255, 0.8);
  background: #232323;
  border-radius: 0;
  border: none;
  margin: 0;
}
.tooltip {
  white-space: nowrap;
}
.tooltip .keystroke {
  font-family: Helvetica, Arial, sans-serif;
  font-size: 13px;
  color: #777;
  padding-left: 2px;
}
.tooltip.in {
  opacity: 1;
}
.tooltip .tooltip-inner {
  line-height: 19px;
  border-radius: 0;
  background-color: #dcdcdc;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
  color: #1f1f1f;
  white-space: nowrap;
  max-width: none;
}
.tooltip .tooltip-inner .keystroke {
  color: #1f1f1f;
}
.tooltip .tooltip-inner .keystroke:before {
  content: '(';
}
.tooltip .tooltip-inner .keystroke:after {
  content: ')';
}
.tooltip.top .tooltip-arrow {
  border-top-color: #dcdcdc;
}
.tooltip.top-left .tooltip-arrow {
  border-top-color: #dcdcdc;
}
.tooltip.top-right .tooltip-arrow {
  border-top-color: #dcdcdc;
}
.tooltip.right .tooltip-arrow {
  border-right-color: #dcdcdc;
}
.tooltip.left .tooltip-arrow {
  border-left-color: #dcdcdc;
}
.tooltip.bottom .tooltip-arrow {
  border-bottom-color: #dcdcdc;
}
.tooltip.bottom-left .tooltip-arrow {
  border-bottom-color: #dcdcdc;
}
.tooltip.bottom-right .tooltip-arrow {
  border-bottom-color: #dcdcdc;
}
.tree-view {
  background: #333333;
  padding: 12px;
}
.tree-view:before {
  content: 'folders';
  text-transform: uppercase;
  display: inline-block;
  margin-bottom: 5px;
  font-size: 11px;
  color: #787878;
  font-weight: bold;
}
.list-tree {
  line-height: 16px !important;
  font-size: 11px;
  vertical-align: middle;
}
.list-tree.has-collapsable-children .list-nested-item > .list-tree > li,
.list-tree.has-collapsable-children .list-nested-item > .list-group > li {
  padding-left: 12px;
}
.list-tree .selected:before {
  background-color: transparent;
}
.list-tree li:not(.list-nested-item) {
  color: #8c8c8c;
}
.list-tree li.list-nested-item.status-modified .list-item,
.list-tree li.list-nested-item .status-ignored.list-item {
  color: #8c8c8c;
}
.list-tree li.list-nested-item.directory .list-item:not(.file),
.list-tree li.list-nested-item.selected.directory .list-item:not(.file) {
  color: #d2d2d2;
}
.list-tree li.list-nested-item .list-item:hover,
.list-tree li.list-nested-item.selected .list-item:hover {
  color: white !important;
}
.list-tree li.list-nested-item .header:before {
  display: none !important;
}
.list-tree li.list-nested-item.expanded .icon-repo:before,
.list-tree li.list-nested-item.expanded .icon-file-directory:before {
  background-image: -webkit-image-set(url("atom://indico-ui/images/group-open.png") 1x, url("atom://indico-ui/images/group-open@2x.png") 2x);
  content: " ";
  width: 24px;
  height: 16px;
  margin-right: 0 !important;
  top: 4px;
}
.list-tree li.list-nested-item.collapsed .icon-repo:before,
.list-tree li.list-nested-item.collapsed .icon-file-directory:before {
  background-image: -webkit-image-set(url("atom://indico-ui/images/group-closed.png") 1x, url("atom://indico-ui/images/group-closed@2x.png") 2x);
  content: " ";
  width: 24px;
  height: 16px;
  margin-right: 0 !important;
  top: 4px;
}
.list-tree li.list-nested-item.expanded .icon-file-symlink-directory:before {
  background-image: -webkit-image-set(url("atom://indico-ui/images/icon-file-symlink-directory.png") 1x, url("atom://indico-ui/images/icon-file-symlink-directory@2x.png") 2x);
  content: " ";
  width: 24px;
  height: 16px;
  margin-right: 0 !important;
  top: 4px;
}
.list-tree li.list-nested-item.collapsed .icon-file-symlink-directory:before {
  background-image: -webkit-image-set(url("atom://indico-ui/images/icon-file-symlink-directory.png") 1x, url("atom://indico-ui/images/icon-file-symlink-directory@2x.png") 2x);
  content: " ";
  width: 24px;
  height: 16px;
  margin-right: 0 !important;
  top: 4px;
}
.list-tree .list-item {
  transition: color 100ms ease-in-out;
}
.list-tree .list-item .icon-file-text:before,
.list-tree .list-item .icon-file-media:before,
.list-tree .list-item .icon-book:before {
  content: '';
  width: 0;
  margin-right: 0 !important;
}
.list-tree li:not(.list-nested-item).selected {
  color: white !important;
}
.list-tree li:not(.list-nested-item).selected:before {
  background-color: #2c2c2c;
}
.list-tree .file {
  margin-left: 24px !important;
}
